}; template <class S, S (*op)(S, S), S (*e)()> struct RangeBST {
private: using NC = Node<S, op, e>; NC *root, *min_, *max_; void splay(NC *node) { node->splay(), root = node; } NC* bound(i64 x, bool lower) { NC *valid = nullptr, *left = root, *right = nullptr; while (left) { valid = left; if ((lower && !(x > left->pt)) || (!lower && (x < left->pt))) { right = left; left = left->l; } else left = left->r; } if (!right && valid) splay(valid); return right; } void set(i64 x, S val, bool add) { NC *nn = new NC(x, val); // if no nodes in tree if (!root) { min_ = nn, max_ = nn, root = nn; return; } if (min_->pt > x) { // if x become min key in tree min_->l = nn, nn->p = min_, min_ = nn; splay(nn); return; } if (max_->pt < x) { // if x become max key in tree max_->r = nn, nn->p = max_, max_ = nn; splay(nn); return; } NC *node = bound(x, true); // assert node is not null if (node->pt == x) { // if tree already has key x if (add) node->v = op(node->v, val); else node->v = val; node->update(); splay(node); delete nn; return; } // now node is first node whose key is larger than x nn->l = node->l; node->l = nn; nn->p = node; if (nn->l) nn->l->p = nn; nn->update(); splay(nn); }
